private class Book {
    private double price;
     public double getPrice() {
         return price;
    } 
     public void setPrice(double price) {
         this.price = price;
     }
     public int compareByPrice(Book b1, Book b2){ 
        double diff = b2.getPrice() - b1.getPrice();
         if (diff == 0) return (int)diff;
         return (diff > 0 ? 1 : -1);
    }
}
Arrays.sort(library, (b1, b2) -> {
    return Book.compareByPrice(b1, b2); });
// Can be replaced by
Arrays.sort(library, Book::compareByPrice);

